css:
.first_block{ margin-top:11%; }
.invoice_no{
  margin-left:10%;
  color:#6E6E6E;
  font-size:15px;
  font-weight: bold;
}
.commercail_invoice, .lpo_no{
  font-size:15px;
  font-weight: bold;
}

.narration{
  margin-left:5%;
  font-size:12px;
  border: 1px solid #e3e3e3;
  overflow: hidden;
}
.table-borderless td,
.table-borderless th {
    border: 0;
}

.total{
  align-content: right;
}

@media print {

   body{
    width: 23cm;

    font-size:17px;
    margin:0px;

   }
  .invoice_no{
    position: fixed;
    top: 8%;
    left: 1px;
    width: 8cm;

  }

  .date{
    position: fixed;
    /*top: 4.8cm;*/
    top:11%;
    left: 0%;
    width: 8cm;
    font-size:17px;
  }
  .customer{
     position: fixed;
     top: 13%;
     left: 0%;
     width: 7cm;
     font-size:17px;
   }
   .commercail_invoice{
     position: fixed;
     top: 12%;
     left: 38%;
     width: 10cm;
     font-size:19px;
   }

   .lpo_no{
     position: fixed;
     top:11%;
     right: 1%;
     width: 10cm;
     color:#6E6E6E;
     font-size:15px;
     font-weight: bold;
    }
   .table{
     position: fixed;
     width: 110%;
     top: 27%;
     font-size:17px;
   }

  .narration{
     position: fixed;
     top: 55%;
   }
  .in_words{
    position: fixed;
      bottom: 24%;
      right: 1;
      width: 12cm;
      font-size:17px;
  }

  .total{
    position: fixed;
      top: 71%;
      right: 0;
      width: 5cm;
      text-align: right;
      font-size:17px;
  }
  .lastrow{
    margin-top:5px;
  }

}
